import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-display-main',
  templateUrl: './display-main.component.html',
  styleUrls: ['./display-main.component.css']
})
export class DisplayMainComponent {

  @Input() card: any;
  cards = [
    {
      projectId: '1',
      projectName: 'Project 1',
      manager: 'John Doe',
      description: 'This is Project 1 description.'
    },
    {
      projectId: '2',
      projectName: 'Project 2',
      manager: 'Jane Smith',
      description: 'This is Project 2 description.'
    },
    // Add more cards as needed
  ];

}
