import { Component, EventEmitter, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit{
  ngOnInit(): void {
  }

  @Output() sideNavToggled = new EventEmitter<boolean>();
  menuStatus: boolean= false;

SideNavToggle(){
  this.menuStatus=!this.menuStatus;
  this.sideNavToggled.emit(this.menuStatus);
}
}
<nav class="navbar navbar-expand navbar-dark bg-dark px-2">
    <div class="side-nav-button p-2 me-3 text-light" (click)="SideNavToggle()">
        <i class="fa-solid fa-bars"></i>
    </div>

    <a class ="navbar-brand px-4" href="#">Navbar</a>
    <div class="navbar-collapse">
        <ul class="navbar-nav me-auto">
            <li class="nav-item dropdown">
                <a class="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown">
                    Menu 1
                </a>
                <ul class="dropdown-menu dropdown-menu-dark">
                    <li><a class="dropdown-item" href="#">child 1</a></li>
                </ul>
            </li>
        </ul>
    </div>
    <form class="d-flex me-5">
        <input class="form-control me-2" type="search" placeholder="Search">
        <button class="btn btn-outline-light" type="submit">Search</button>
    </form>
    <div class="profile-logo dropstart">
        <button class="btn btn-outline-light dropdown-toggle" data-bs-toggle="dropdown">
            Profile
        </button>
        <ul class="dropdown-menu dropdown-menu-dark">
            <li><a  href="#" class="dropdown-item">My Profile</a></li>
            <li><a  href="#" class="dropdown-item">Log out</a></li>
            <!-- <li><a  href="#" class="dropdown-item">Sign Up</a></li> -->
        </ul>

    </div>
</nav>
