import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'bug-app-front-end';
  sideNavStatus:boolean=false;

  cards = [
    {
      projectId: '1',
      projectName: 'Project 1',
      manager: 'John Doe',
      description: 'This is Project 1 description.'
    },
    {
      projectId: '2',
      projectName: 'Project 2',
      manager: 'Jane Smith',
      description: 'This is Project 2 description.'
    },
    {
      projectId: '2',
      projectName: 'Project 2',
      manager: 'Jane Smith',
      description: 'This is Project 2 description.'
    }
    // Add more cards as needed
  ];
}
<div class="container-fluid">
  <app-header (sideNavToggled)=" sideNavStatus= $event;"></app-header>

  <main>
    <app-side-nav [sideNavStatus]="sideNavStatus" 
    [ngClass]="{'app-side-nav-open':sideNavStatus}"> </app-side-nav>

    <div class="display-area p-3" [ngClass]="
    {'display-area-shrink':sideNavStatus}">
    <!-- <app-display-main
    *ngFor="let card of cards"
    [projectId]="card.projectId"
    [projectName]="card.projectName"
    [manager]="card.manager"
    [description]="card.description"
  ></app-display-main> -->


  <div class="card-container">
    <div class="card-wrapper" *ngFor="let card of cards">
      <app-display-main [card]="card">





      </app-display-main>

    </div>
  </div>




    </div>
  </main>
</div>
